import React from 'react';
import ShowReminders from './reminders.js';
import AddReminder from './addReminders.js';
import {HandleReminderChange} from './addReminders.js';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      reminders: [
        {
          name: 'Buy some eggs',
          timestamp: "2018-11-10T13:00:00.141Z"
        }
      ],
      newReminder: ''
    }
  }

  render() {
    return (
      <div>
        <h2>Reminders</h2>

        <form onSubmit={AddReminder}>
          <div>
            Topic: 
            <input
              value={this.state.newReminder}
              onChange={HandleReminderChange}
            />
          </div>
          <div>
            <button type="submit">Add</button>
          </div>
        </form>

        <ShowReminders reminders={this.state.reminders} />
      </div>

    )
  }
}

export default App
