import React from 'react';
import ShowReminders from './reminders.js';

class AddReminders extends React.Component {
    constructor(props) {  /* voiko constructorin jättää pois, kokeile */
      super(props);
/*      this.state = {
        reminders: props.reminders,
        newReminder: ''
      }*/
    }

  addReminder = (event) => {
    /*alert('A note was submitted: ' + this.state.newNote);*/
    event.preventDefault()
    const reminderObject = {
      name: this.state.newReminder,
/*      date: new Date().toISOString(),*/
      id: this.state.reminders.length + 1
    }
    console.log(reminderObject)
    const reminders = this.state.reminders.concat(reminderObject)
    console.log(reminders)
    this.props.setReminder({
      reminders: reminders,
      newReminder: ''
    })

    console.log('nappia painettu')
    console.log(event.target)
  }

  handleReminderChange = (event) => {
    console.log(event.target.value)
    this.setState({ newReminder: event.target.value })
  }

  render () {

    /* voisiko tästä tehdä vain funktion? */
    return (
      <div>
        <form onSubmit={this.addReminder}>
            <div>
              Topic: 
              <input
                value={this.state.newReminder}
                onChange={this.handleReminderChange}
              />
            </div>
            <div>
              <button type="submit">Add</button>
            </div>
        </form>
        <ShowReminders reminders={this.state.reminders} />
      </div>
    )
  }
}

export default AddReminders
